import React, { Component } from "react";
import Display from "./Components/Display";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      users: [],
      oops: "",
      check: false
    };
  }

  SubmitHandler = e => {
    e.preventDefault();
    fetch("https://jsonplaceholder.typicode.com/users")
      .then(res => res.json())
      .then(data => {
        console.log(data);
        var str = "";
        this.setState({ users: data, oops: str, check: true });
      });
  };
  ClickmeHandler = () => {
    if (this.state.check) {
      var arr = [];
      this.setState({ users: arr, oops: "Oooooooops!!!!!" });
    } else {
      var str = "First submit the query!!";
      this.setState({ oops: str });
    }
  };
  render() {
    return (
      <div>
        <header className="heading">
          <h1>Doodle Search</h1>
        </header>
        <form onSubmit={this.SubmitHandler}>
          <div className="wrap">
            <input
              value={this.state.search}
              onChange={this.setState({ search: e.target.value })}
              className="input_box"
              type="text"
              placeholder="Ask a question"
            />
            <button className="search_icon" type="submit">
              <i className="fa fa-search" />
            </button>
          </div>
        </form>
        <div className="btn-wrap">
          <button className="btn btn-1">Rotate me</button>
          <button className="btn btn-2">Revolve</button>
          <button className="btn btn-3" onClick={this.ClickmeHandler}>
            Click me
          </button>
          <button className="btn btn-4">Hover me</button>
        </div>
        <Display users={this.state.users} />
        <div className="oops">{this.state.oops}</div>
        <footer>&copy; 2019 All Rights Reserved</footer>
      </div>
    );
  }
}

export default App;
